    private void panggilLaporan(String teks) {
        try{
            File g = new File("fileklaim.css");            
            try (BufferedWriter bg = new BufferedWriter(new FileWriter(g))) {
                bg.write(
                        "@media print{.pagebreak{page-break-before: always;}}"
                );
            }
            File f = new File("generateberkasklaim.html");            
            BufferedWriter bw = new BufferedWriter(new FileWriter(f));
            bw.write(
                 teks.replaceAll("<head>","<head><link href=\"fileklaim.css\" rel=\"stylesheet\" type=\"text/css\" media=\"all\"/><meta http-equiv=\"Access-Control-Allow-Origin\" content=\"*\" />")
            );  
            bw.close();
            Desktop.getDesktop().browse(f.toURI());
        } catch (Exception e) {
            System.out.println("Notifikasi : "+e);
        }   
    }
    
